import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { userPayments } from '../../actions/authAction';  
import { withRouter } from 'react-router-dom';
import TextFieldGroup from '../common/TextFieldGroup';
import PayImage  from '../../assets/image/btn_buynowCC_LG.gif';
import scriptLoader from 'react-async-script-loader';

class Payments extends Component {
  constructor() {  
    super();
    this.state= {
      name: '',
    //   email: '',
    //   password: '',
    //   password2: '',  
    //   errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  componentWillReceiveProps(nextProps){
    if(nextProps.errors){
      this.setState({errors: nextProps.errors})
    }
  }
  onSubmit(e){
    e.preventDefault();
    const userPay = {
      name: this.state.name,      
    }; 
    console.log(userPay); 
    this.props.userPayments(userPay, this.props.history);

    }
  onChange(e){
    this.setState({ [e.target.name]: e.target.value});
  }
  render(){
    const { errors } = this.state;

    return (
      <div>
      <div className="container-fluid">
          <div className="banner-section">
              <div className="banner-title">
                  <h1>Now, You can pay</h1>
              </div>
          </div>

      </div>
      <form>
      <TextFieldGroup
            placeholder="Name"
            name="name"
            type="name"
            value={this.state.name}
            onChange={this.onChange}
            // error={errors.name}
            />
            <input type="submit" className="btn btn-info" />
      </form>

      </div>
    );
  }
} 
Payments.propTypes = {
  userPayments: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
})

export default scriptLoader('https://www.paypalobjects.com/api/checkout.js')(Payments);
